using System;

namespace Euler8
{
    class Program
    {
        static string text = "";
        static void Main(string[] args)
        {
            long[] array = new long[1000];
            int number = 0;
            int number1 = 14;
            int number2 = 34;
            while(number2 <1000)
             {
                long multiply = 1;
                for (int i = number1; i <= number2; i++)
                {
                    int n = int.Parse(text[i].ToString());
                    multiply *= n;
                }

                array[number] = multiply;
                 Console.WriteLine(array[number]);
                 ++number;
                 ++number1;
                 ++number2;
             }

            Console.WriteLine($"Max = {Max(array)}");
           
        }

        static long Max(long[] array)
        {
            long max = array[0];
            for(int i = 1; i < array.Length; i++)
                if (array[i] > max)
                {
                    max = array[i];
                }
            return max;
        }
